import React, { useState, useEffect, useRef } from "react";

const Header = () => {
  const [isSearchOpen, setIsSearchOpen] = useState(false);
  const [isNotificationOpen, setIsNotificationOpen] = useState(false);
  const [isMessageOpen, setIsMessageOpen] = useState(false);
  const [isAppsOpen, setIsAppsOpen] = useState(false);
  const [isUserMenuOpen, setIsUserMenuOpen] = useState(false);

  const searchRef = useRef(null);
  const notificationRef = useRef(null);
  const messageRef = useRef(null);
  const appsRef = useRef(null);
  const userMenuRef = useRef(null);

  // Handle click outside to close dropdowns
  useEffect(() => {
    const handleClickOutside = (event) => {
      if (searchRef.current && !searchRef.current.contains(event.target)) {
        setIsSearchOpen(false);
      }
      if (
        notificationRef.current &&
        !notificationRef.current.contains(event.target)
      ) {
        setIsNotificationOpen(false);
      }
      if (messageRef.current && !messageRef.current.contains(event.target)) {
        setIsMessageOpen(false);
      }
      if (appsRef.current && !appsRef.current.contains(event.target)) {
        setIsAppsOpen(false);
      }
      if (userMenuRef.current && !userMenuRef.current.contains(event.target)) {
        setIsUserMenuOpen(false);
      }
    };

    document.addEventListener("mousedown", handleClickOutside);
    return () => {
      document.removeEventListener("mousedown", handleClickOutside);
    };
  }, []);

  // Toggle functions for each dropdown
  const toggleSearch = () => setIsSearchOpen(!isSearchOpen);
  const toggleNotification = () => {
    setIsNotificationOpen(!isNotificationOpen);
    setIsMessageOpen(false);
    setIsAppsOpen(false);
    setIsUserMenuOpen(false);
  };
  const toggleMessage = () => {
    setIsMessageOpen(!isMessageOpen);
    setIsNotificationOpen(false);
    setIsAppsOpen(false);
    setIsUserMenuOpen(false);
  };
  const toggleApps = () => {
    setIsAppsOpen(!isAppsOpen);
    setIsNotificationOpen(false);
    setIsMessageOpen(false);
    setIsUserMenuOpen(false);
  };
  const toggleUserMenu = () => {
    setIsUserMenuOpen(!isUserMenuOpen);
    setIsNotificationOpen(false);
    setIsMessageOpen(false);
    setIsAppsOpen(false);
  };

  return (
    <header>
      <div classNameName="topbar d-flex align-items-center">
        <nav classNameName="navbar navbar-expand">
          <div classNameName="mobile-toggle-menu">
            <i classNameName="bx bx-menu"></i>
          </div>

          {/* Search Bar */}
          <div classNameName="search-bar flex-grow-1" ref={searchRef}>
            <div classNameName="position-relative search-bar-box">
              <input
                type="text"
                classNameName={`form-control search-control ${
                  isSearchOpen ? "active" : ""
                }`}
                placeholder="Type to search..."
              />
              <span
                classNameName="position-absolute top-50 search-show translate-middle-y"
                onClick={toggleSearch}
              >
                <i classNameName="bx bx-search"></i>
              </span>
              <span
                classNameName="position-absolute top-50 search-close translate-middle-y"
                onClick={toggleSearch}
              >
                <i classNameName="bx bx-x"></i>
              </span>
            </div>
          </div>

          <div classNameName="top-menu ms-auto">
            <ul classNameName="navbar-nav align-items-center">
              {/* Apps Dropdown */}
              <li classNameName="nav-item dropdown dropdown-large" ref={appsRef}>
                <a
                  classNameName={`nav-link dropdown-toggle dropdown-toggle-nocaret ${
                    isAppsOpen ? "show" : ""
                  }`}
                  href="#"
                  onClick={(e) => {
                    e.preventDefault();
                    toggleApps();
                  }}
                >
                  <i classNameName="bx bx-category"></i>
                </a>
                <div
                  classNameName={`dropdown-menu dropdown-menu-end ${
                    isAppsOpen ? "show" : ""
                  }`}
                >
                  {/* Apps dropdown content remains the same */}
                </div>
              </li>

              {/* Notifications Dropdown */}
              <li
                classNameName="nav-item dropdown dropdown-large"
                ref={notificationRef}
              >
                <a
                  classNameName={`nav-link dropdown-toggle dropdown-toggle-nocaret position-relative ${
                    isNotificationOpen ? "show" : ""
                  }`}
                  href="#"
                  onClick={(e) => {
                    e.preventDefault();
                    toggleNotification();
                  }}
                >
                  <span classNameName="alert-count">7</span>
                  <i classNameName="bx bx-bell"></i>
                </a>
                <div
                  classNameName={`dropdown-menu dropdown-menu-end ${
                    isNotificationOpen ? "show" : ""
                  }`}
                >
                  {/* Notifications dropdown content remains the same */}
                </div>
              </li>

              {/* Messages Dropdown */}
              <li classNameName="nav-item dropdown dropdown-large" ref={messageRef}>
                <a
                  classNameName={`nav-link dropdown-toggle dropdown-toggle-nocaret position-relative ${
                    isMessageOpen ? "show" : ""
                  }`}
                  href="#"
                  onClick={(e) => {
                    e.preventDefault();
                    toggleMessage();
                  }}
                >
                  <span classNameName="alert-count">8</span>
                  <i classNameName="bx bx-comment"></i>
                </a>
                <div
                  classNameName={`dropdown-menu dropdown-menu-end ${
                    isMessageOpen ? "show" : ""
                  }`}
                >
                  {/* Messages dropdown content remains the same */}
                </div>
              </li>
            </ul>
          </div>

          {/* User Profile Dropdown */}
          <div classNameName="user-box dropdown" ref={userMenuRef}>
            <a
              classNameName={`d-flex align-items-center nav-link dropdown-toggle dropdown-toggle-nocaret ${
                isUserMenuOpen ? "show" : ""
              }`}
              href="#"
              onClick={(e) => {
                e.preventDefault();
                toggleUserMenu();
              }}
            >
              <img
                src="assets/images/avatars/avatar-2.png"
                classNameName="user-img"
                alt="user avatar"
              />
              <div classNameName="user-info ps-3">
                <p classNameName="user-name mb-0">Pauline Seitz</p>
                <p classNameName="designattion mb-0">Web Designer</p>
              </div>
            </a>
            <ul
              classNameName={`dropdown-menu dropdown-menu-end ${
                isUserMenuOpen ? "show" : ""
              }`}
            >
              <li>
                <a className="dropdown-item" href="javascript:;">
                  <i className="bx bx-user"></i>
                  <span>Profile</span>
                </a>
              </li>
              <li>
                <a className="dropdown-item" href="javascript:;">
                  <i className="bx bx-cog"></i>
                  <span>Settings</span>
                </a>
              </li>
              <li>
                <a className="dropdown-item" href="javascript:;">
                  <i className="bx bx-home-circle"></i>
                  <span>Dashboard</span>
                </a>
              </li>
              <li>
                <a className="dropdown-item" href="javascript:;">
                  <i className="bx bx-dollar-circle"></i>
                  <span>Earnings</span>
                </a>
              </li>
              <li>
                <a className="dropdown-item" href="javascript:;">
                  <i className="bx bx-download"></i>
                  <span>Downloads</span>
                </a>
              </li>
              <li>
                <div className="dropdown-divider mb-0"></div>
              </li>
              <li>
                <a className="dropdown-item" href="javascript:;">
                  <i className="bx bx-log-out-circle"></i>
                  <span>Logout</span>
                </a>
              </li>
            </ul>
          </div>
        </nav>
