import axios from "axios";
import React, { useEffect, useState } from "react";

const AddMenu = () => {
  const [menuName, setMenuName] = useState("");
  const [category, setCategory] = useState("");
  const [price, setPrice] = useState("");
  const [description, setDescription] = useState("");

  const [error, setError] = useState("");
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    const fetchCustomers = async () => {
      try {
        const response = await axios.get("http://localhost:8000/api/category");
        setCategory(response.data.categories);
        console.log("data is:", response.data.categories);
        setLoading(false);
      } catch (err) {
        setError("Failed to fetch customers.");
        setLoading(false);
      }
    };

    fetchCustomers();
  }, []);

  const handleSubmit = (e) => {
    e.preventDefault(); // Prevent form submission before validation

    // Reset error message
    setError("");

    // Validation
    if (!menuName || !category || !price || !description) {
      setError("All fields are required.");
      return;
    }

    // Proceed with form submission (e.g., make API request)
    setLoading(true);

    // Example of a successful submit action
    setTimeout(() => {
      setLoading(false);
      alert("Menu added successfully!");
      // Optionally, clear form after submission
      setMenuName("");
      setCategory("");
      setPrice("");
      setDescription("");
    }, 2000);
  };

  return (
    <div className="page-content">
      <div className="page-breadcrumb d-none d-sm-flex align-items-center mb-3">
        <div className="breadcrumb-title pe-3">Add Menu</div>
        <div className="ps-3">
          <nav aria-label="breadcrumb">
            <ol className="breadcrumb mb-0 p-0">
              <li className="breadcrumb-item">
                <a href="">
                  <i className="bx bx-home-alt"></i>
                </a>
              </li>
              <li className="breadcrumb-item active" aria-current="page">
                Menu Profile
              </li>
            </ol>
          </nav>
        </div>
      </div>

      <div className="container">
        <div className="main-body">
          <div className="row">
            <div className="col-lg-8">
              <div className="card">
                <form onSubmit={handleSubmit}>
                  <div className="card-body">
                    {/* Menu Name */}
                    <div className="row mb-3">
                      <div className="col-sm-3">
                        <h6 className="mb-0">Name</h6>
                      </div>
                      <div className="col-sm-9 text-secondary">
                        <input
                          type="text"
                          className="form-control"
                          placeholder="Enter name"
                          value={menuName}
                          onChange={(e) => setMenuName(e.target.value)}
                        />
                      </div>
                    </div>

                    /* Category */
                    <div className="row mb-3">
                      <div className="col-sm-3">
                        <h6 className="mb-0">Category</h6>
                      </div>
                      <div className="col-sm-9 text-secondary">
                        <select
                          className="form-select mb-3"
                          value={category}
                          onChange={(e) => setCategory(e.target.value)}
                        >
                          <option value="">Select Category</option>
                          {category.map((cat) => (
                            <option key={cat.category_id} value={cat.category_id}>
                              {cat.category_name}
                            </option>
                          ))}
                        </select>
                      </div>
                    </div>}
                    <div className="row mb-3">
                      <div className="col-sm-3">
                        <h6 className="mb-0">Price</h6>
                      </div>
                      <div className="col-sm-9 text-secondary">
                        <input
                          type="text"
                          className="form-control"
                          placeholder="Enter Price"
                          value={price}
                          onChange={(e) => setPrice(e.target.value)}
                        />
                      </div>
                    </div>

                    {/* Description */}
                    <div className="row mb-3">
                      <div className="col-sm-3">
                        <h6 className="mb-0">Description</h6>
                      </div>
                      <div className="col-sm-9 text-secondary">
                        <textarea
                          className="form-control"
                          placeholder="Enter Description"
                          rows="3"
                          value={description}
                          onChange={(e) => setDescription(e.target.value)}
                        ></textarea>
                      </div>
                    </div>

                    {/* Error Message */}
                    {error && (
                      <div className="alert alert-danger" role="alert">
                        {error}
                      </div>
                    )}

                    {/* Submit Button */}
                    <div className="row">
                      <div className="col-sm-3"></div>
                      <div className="col-sm-9 text-secondary">
                        <button
                          type="submit"
                          className="btn btn-primary px-4"
                          disabled={loading}
                        >
                          {loading ? "Adding..." : "Add Menu"}
                        </button>
                      </div>
                    </div>
                  </div>
                </form>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default AddMenu;
