import React, { useEffect, useRef } from "react";
import $ from "jquery";
import "datatables.net-bs5";
import "datatables.net-buttons-bs5";
import "datatables.net-buttons/js/buttons.html5.js";
import "datatables.net-buttons/js/buttons.print.js";

const OrderDetails = () => {
  const tableRef = useRef(null);
  const tableInstance = useRef(null);

  useEffect(() => {
    // Initialize DataTable only if it hasn't been initialized yet
    if (tableRef.current && !tableInstance.current) {
      tableInstance.current = $(tableRef.current).DataTable({
        lengthChange: false,
        buttons: ["copy", "excel", "pdf", "print"],
        destroy: true, // This ensures the table can be reinitialized if needed
      });

      // Move buttons to the correct container
      tableInstance.current
        .buttons()
        .container()
        .appendTo("#example2_wrapper .col-md-6:eq(0)");
    }

    // Cleanup function
    return () => {
      if (tableInstance.current) {
        tableInstance.current.destroy();
        tableInstance.current = null;
      }
    };
  }, []); // Empty dependency array means this effect runs once on mount

  return (
    <div className="card">
      <div className="card-body">
        <div className="table-responsive">
          <table
            id="example2"
            ref={tableRef}
            className="table table-striped table-bordered"
          >
            <thead>
              <tr>
                <th>Name</th>
                <th>Position</th>
                <th>Office</th>
                <th>Age</th>
                <th>Start date</th>
                <th>Salary</th>
              </tr>
            </thead>
            <tbody>
              <tr>
                <td>Airi Satou</td>
                <td>Accountant</td>
                <td>Tokyo</td>
                <td>33</td>
                <td>2008/11/28</td>
                <td>$162,700</td>
              </tr>
              {/* ... other rows ... */}
            </tbody>
            <tfoot>
              <tr>
                <th>Name</th>
                <th>Position</th>
                <th>Office</th>
                <th>Age</th>
                <th>Start date</th>
                <th>Salary</th>
              </tr>
            </tfoot>
          </table>
        </div>
      </div>
    </div>
  );
};

export default OrderDetails;
